package com.adventofcode.day6;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.Test;

class TuningTroubleTest {

    @Test
    public void shouldReturnCorrectMarkerForInputString1() {
        TuningTrouble tuningTrouble = new TuningTrouble();
        String inputString = "bvwbjplbgvbhsrlpgdmjqwftvncz";
        int minimumUniqueCharactersInMsg = 4;
        int expectedMarker = 5;

        int actualMarker = tuningTrouble.getFirstMarker(inputString, minimumUniqueCharactersInMsg);

        Assertions.assertEquals(expectedMarker, actualMarker);
    }

    @Test
    public void shouldReturnCorrectMarkerForMsgLengthOf4() {
        TuningTrouble tuningTrouble = new TuningTrouble();
        int minimumUniqueCharactersInMsg = 4;
        String inputString = "";
        int expectedMarker = 1920;

        int actualMarker = tuningTrouble.getFirstMarker(inputString, minimumUniqueCharactersInMsg);

        Assertions.assertEquals(expectedMarker, actualMarker);
    }

    @Test
    public void shouldReturnCorrectMarkerForMsgLengthOf14() {
        TuningTrouble tuningTrouble = new TuningTrouble();
        int minimumUniqueCharactersInMsg = 14;
        String inputString = "";
        int expectedMarker = 2334;

        int actualMarker = tuningTrouble.getFirstMarker(inputString, minimumUniqueCharactersInMsg);

        Assertions.assertEquals(expectedMarker, actualMarker);
    }
}